g:rowValidation.ui:function:
if (!(row.Company || row.Category || row.Item || row.Model || row.Job)) {
return $wnd.Bee_maybeTranslate("=company+, +=category+, +=item+, +=vehicleModel+, +=serviceJob+: +=allValuesCannotBeEmpty");
}
if (!(row.PriceName || row.Percent || row.Price > 0)) {
return $wnd.Bee_maybeTranslate("=priceName+, +=discountPercent+, +=discountPrice+: +=allValuesCannotBeEmpty");
}
return null;
